import React, { useState } from "react";
import CalculatorButton from "./CalculatorButton"

function Calculator() {
  const [num, setNum] = useState(0);

  function handleNum(event) {
    var input = event.target.value;
    if (num === 0) {
      setNum(input)
    } else {
      setNum(num + input)
    }
  }

  function clear() {
    setNum(0)
  }

  return (
    <div className="calculator">
      <div className="calculator-result">
        <div className="result">0</div>
      </div>
      <div className="calculator-keypad">
        <div className="keypad-functions">
         <CalculatorButton value="H" />
         <CalculatorButton onClick={clear} value="C" />
         <CalculatorButton onClick={percentage} value="%" />
        </div>
        <div className="keypad-operators">
         <CalculatorButton value="+" />
         <CalculatorButton value="-" />
         <CalculatorButton value="*" />
         <CalculatorButton value="/" />
         <CalculatorButton value="=" />
        </div>
        <div className="keypad-numbers">
         <CalculatorButton onClick={handleNum} value="9" />
         <CalculatorButton onClick={handleNum} value="8" />
         <CalculatorButton onClick={handleNum} value="7" />
         <CalculatorButton onClick={handleNum} value="6" />
         <CalculatorButton onClick={handleNum} value="5" />
         <CalculatorButton onClick={handleNum} value="4" />
         <CalculatorButton onClick={handleNum} value="3" />
         <CalculatorButton onClick={handleNum} value="2" />
         <CalculatorButton onClick={handleNum} value="1" />
        </div>
        <div className="keypad-zero">
        <CalculatorButton value="0" />
        </div>
      </div>
    </div>
  )

}

export default Calculator;
