import React, { useState } from "react";
import CalculatorButton from "./CalculatorButton"

function Calculator() {
  const [prevValue, setPrevValue] = useState(0);
  const [nextValue, setNextValue] = useState("");
  const [operator, setOperator] = useState(null);

  const CalculatorOperations = {
    "+": (firstValue, secondValue) => firstValue + secondValue,
    "-": (firstValue, secondValue) => firstValue - secondValue,
    "*": (firstValue, secondValue) => firstValue * secondValue,
    "/": (firstValue, secondValue) => firstValue / secondValue,
    "=": (firstValue, secondValue) => secondValue
  }

  function performOperation() {
    let temp = CalculatorOperations[operator](
      parseFloat(prevValue),
      parseFloat(nextValue)
    );
    setOperation(null);
    setNextValue(String(temp));
    setPrevValue(null);
  }

  return (
    <div className="calculator">
      <div className="calculator-result">
        <div className="result">0</div>
      </div>
      <div className="calculator-keypad">
        <div className="keypad-functions">
         <CalculatorButton value="H" />
         <CalculatorButton value="C" />
         <CalculatorButton value="%" />
        </div>
        <div className="keypad-operators">
         <CalculatorButton value="+" />
         <CalculatorButton value="-" />
         <CalculatorButton value="*" />
         <CalculatorButton value="/" />
         <CalculatorButton value="=" />
        </div>
        <div className="keypad-numbers">
         <CalculatorButton value="9" />
         <CalculatorButton value="8" />
         <CalculatorButton value="7" />
         <CalculatorButton value="6" />
         <CalculatorButton value="5" />
         <CalculatorButton value="4" />
         <CalculatorButton value="3" />
         <CalculatorButton value="2" />
         <CalculatorButton value="1" />
        </div>
        <div className="keypad-zero">
        <CalculatorButton value="0" />
        </div>
      </div>
    </div>
  )

}

export default Calculator;
